"use strict";const draw=()=>{const ctx=document.getElementById("canvas").getContext("2d"),img=document.getElementById("model"),text=document.getElementById("text"),size=document.getElementById("size"),save=document.getElementById("save"),sizePreview=document.querySelector(".text-size-preview");sizePreview.textContent=size.value,ctx.canvas.width=img.width,ctx.canvas.height=img.height,img.onload=()=>{ctx.drawImage(img,0,0,img.width,img.height)},img.src="./assets/img/raoult-kenobi.jpg";const redraw=()=>{ctx.font=`${size.value}pt Arial`,ctx.clearRect(0,0,canvas.width,canvas.height),ctx.drawImage(img,0,0,img.width,img.height),ctx.fillStyle="white",ctx.textAlign="center",ctx.fillText(text.value,img.width/2,img.height-40)};text.addEventListener("input",()=>redraw()),size.addEventListener("input",()=>{redraw(),sizePreview.textContent=size.value}),save.addEventListener("click",event=>{event.target.href=ctx.canvas.toDataURL("image/jpeg")},!1)};document.addEventListener("DOMContentLoaded",()=>{draw()});